/**
 * GitHub Scanner for Exposed AWS IAM Keys - Data Processor
 * 
 * This script processes the results from the AWS IAM Key scanner workflow
 * and formats them for reporting and notification purposes.
 */

// Main entry point - this is what n8n will call
// Input: items array from n8n workflow
// Output: Slack Block Kit UI format (blocks array only)
const items = $input.all();

// Process the input data
const scanResults = items.map(item => item.json);
const processedResults = processIAMScanResults(scanResults);

// Format for markdown notification
const markdownNotification = formatForMarkdownNotification(processedResults);
// Return as array of objects for n8n
return [{ message: markdownNotification }];

/**
 * Process scan results and format them for reporting
 * @param {Array} scanResults - Array of scan result objects from the workflow
 * @returns {Array} Array of formatted result objects
 */
function processIAMScanResults(scanResults) {
  if (!Array.isArray(scanResults) || scanResults.length === 0) {
    return [{
      status: 'NO_RESULTS',
      timestamp: new Date().toISOString(),
      message: 'No scan results to process'
    }];
  }

  // Process each scan result
  const processedResults = scanResults.map(result => {
    // Create a deep copy to avoid modifying the original data
    const processedResult = JSON.parse(JSON.stringify(result));

    // Ensure we have the required fields
    if (!processedResult.timestamp) {
      processedResult.timestamp = new Date().toISOString();
    }

    // Calculate risk metrics if not provided
    if (!processedResult.riskLevel) {
      processedResult.riskLevel = calculateRiskLevel(processedResult);
    }

    // Generate recommended actions if not provided
    if (!processedResult.recommendedActions || processedResult.recommendedActions.length === 0) {
      processedResult.recommendedActions = generateRecommendedActions(processedResult);
    }

    return processedResult;
  });

  return processedResults;
}

  /**
   * Calculate risk level based on scan result data
   * @param {Object} result - Individual scan result
   * @returns {string} Risk level (HIGH, MEDIUM, LOW)
   */
  function calculateRiskLevel(result) {
    // Default to MEDIUM if we can't determine
    if (!result || !result.status) {
      return 'MEDIUM';
    }

    // Determine risk level based on status and other factors
    if (result.status === 'COMPROMISED') {
      // Public repositories or repositories with many contributors increase risk
      if (result.repositories && result.repositories.some(repo => {
        return repo.score >= 0.8; // High score indicates higher risk
      })) {
        return 'HIGH';
      }
      return 'MEDIUM';
    } else if (result.status === 'POTENTIAL_EXPOSURE') {
      return 'MEDIUM';
    } else {
      return 'LOW';
    }
  }

  /**
   * Generate recommended actions based on scan result
   * @param {Object} result - Individual scan result
   * @returns {Array} List of recommended actions
   */
  function generateRecommendedActions(result) {
    const commonActions = [
      'Review the exposure details in the security report',
      'Rotate affected access keys immediately'
    ];

    if (result.status === 'COMPROMISED') {
      return [
        ...commonActions,
        'Disable the compromised access key',
        'Generate new access keys for affected services',
        'Remove exposed keys from repositories',
        'Audit recent API usage for this key'
      ];
    } else if (result.status === 'POTENTIAL_EXPOSURE') {
      return [
        ...commonActions,
        'Verify if the exposure is a false positive',
        'Consider rotating keys as a precaution'
      ];
    } else {
      return [
        'No immediate action required',
        'Continue monitoring for potential exposures'
      ];
    }
  }

  /**
   * Format scan results for Slack-compatible markdown notification
   * @param {Array} processedResults - Processed scan results
   * @returns {string} Slack-compatible markdown formatted notification text
   */
  function formatForMarkdownNotification(processedResults) {
    if (!Array.isArray(processedResults) || processedResults.length === 0) {
      return `🔒 *AWS IAM Key Scan Completed*\n\nNo exposed AWS IAM keys were detected in this scan.`;
    }

    // Count compromised keys
    const compromisedCount = processedResults.filter(r => r.status === 'COMPROMISED').length;

    if (compromisedCount === 0) {
      return `🔒 *AWS IAM Key Scan Completed*\n\nNo exposed AWS IAM keys were detected in this scan.`;
    }

    // Create Slack-compatible notification for compromised keys
    let markdown = `⚠️ *ALERT: AWS IAM Keys Exposed*\n\n*${compromisedCount}* AWS IAM keys have been potentially exposed on GitHub. Immediate action required.\n\n`;

    // Add details for each compromised key
    processedResults.filter(r => r.status === 'COMPROMISED').forEach((result, index) => {
      markdown += `*Exposure ${index + 1}*\n\n`;

      // Add repository information in a cleaner format
      markdown += `*Exposure Details:*\n`;
      result.repositories.forEach(repo => {
        markdown += `• *Repository:* ${repo.name}\n`;
        markdown += `• *Path:* \`${repo.path}\`\n`;
        markdown += `• *Risk Score:* ${repo.score.toFixed(2)}\n\n`;
      });

      // Add risk level and action taken in a compact format
      markdown += `*Risk Level:* ${result.riskLevel} | *Action Taken:* ${result.actionTaken || 'None'}\n\n`;

      // Add recommended actions with better formatting
      markdown += `*Recommended Actions:*\n`;
      result.recommendedActions.forEach(action => {
        markdown += `• ${action}\n`;
      });
      markdown += `\n`;
    });

    return markdown;
  }


  // Export functions for use in n8n workflow
  module.exports = {
    processIAMScanResults,
    calculateRiskLevel,
    generateRecommendedActions,
    formatForMarkdownNotification
  };

/**
 * Simplified GitHub Search for AWS Access Keys
 * This script generates optimized search queries for finding exposed AWS access keys on GitHub
 */

/**
 * Generates a GitHub API search URL for a given AWS access key
 * @param {Object} inputData - The input data containing AWS access key information
 * @returns {Object} - Search information including URL and query
 */
function generateGitHubSearchQuery(inputData) {
  try {
    // Extract access key information from input
    let accessKeyId;
    let userName;

    // Handle different input formats
    if (inputData && typeof inputData === 'object') {
      // Check for n8n workflow format
      if (inputData.ListAccessKeysResponse && 
          inputData.ListAccessKeysResponse.ListAccessKeysResult && 
          inputData.ListAccessKeysResponse.ListAccessKeysResult.AccessKeyMetadata && 
          Array.isArray(inputData.ListAccessKeysResponse.ListAccessKeysResult.AccessKeyMetadata) && 
          inputData.ListAccessKeysResponse.ListAccessKeysResult.AccessKeyMetadata.length > 0) {

        accessKeyId = inputData.ListAccessKeysResponse.ListAccessKeysResult.AccessKeyMetadata[0].AccessKeyId;
        userName = inputData.ListAccessKeysResponse.ListAccessKeysResult.AccessKeyMetadata[0].UserName;
      } 
      // Direct object format
      else if (inputData.accessKeyId) {
        accessKeyId = inputData.accessKeyId;
        userName = inputData.userName || 'unknown';
      }
    }

    // Validate we have an access key
    if (!accessKeyId) {
      console.error('No valid AWS access key ID found in input');
      return { error: 'No valid AWS access key ID found in input' };
    }

    // Create the most effective search query
    // Based on testing, the exact match with quotes is most reliable
    const searchQuery = `"${accessKeyId}" in:file`;

    // Generate the GitHub API search URL
    const searchUrl = `https://api.github.com/search/code?q=${encodeURIComponent(searchQuery)}`;

    return {
      accessKeyId,
      userName,
      searchQuery,
      searchUrl,
      note: 'A single exact match query is typically sufficient for finding exposed AWS keys'
    };
  } catch (error) {
    console.error('Error generating GitHub search query:', error);
    return { error: `Error generating GitHub search query: ${error.message}` };
  }
}

/**
 * For more comprehensive searches, this function generates multiple search patterns
 * This is optional and can be used if the simple search doesn't yield results
 * @param {string} accessKeyId - The AWS access key ID to search for
 * @returns {Array} - Array of search queries and URLs
 */
function generateComprehensiveSearch(accessKeyId) {
  if (!accessKeyId) {
    return { error: 'No access key ID provided' };
  }

  // Search patterns for access keys (in order of effectiveness)
  const searchQueries = [
    `"${accessKeyId}" in:file`,           // Exact match (most effective)
    `${accessKeyId} in:file`,              // Without quotes (catches more results but may have false positives)
    `AWS_ACCESS_KEY_ID=${accessKeyId} in:file`,  // Environment variable format
    `aws_access_key_id: ${accessKeyId} in:file`, // YAML/config format
    `accessKeyId: ${accessKeyId} in:file`        // JavaScript/JSON format
  ];

  return searchQueries.map(query => ({
    searchQuery: query,
    searchUrl: `https://api.github.com/search/code?q=${encodeURIComponent(query)}`
  }));
}

// For n8n integration
function processForN8n() {
  try {
    // Get input data
    const inputData = $input.item.json;

    // Generate the simplified search (recommended approach)
    const simpleSearch = generateGitHubSearchQuery(inputData);

    // For backward compatibility, also generate the comprehensive search
    // if an access key was successfully extracted
    let comprehensiveSearch = [];
    if (simpleSearch.accessKeyId) {
      comprehensiveSearch = generateComprehensiveSearch(simpleSearch.accessKeyId);
    }

    return {
      simpleSearch,
      comprehensiveSearch,
      recommendation: 'The simple search is recommended for most cases. Only use comprehensive search if simple search yields no results.'
    };
  } catch (error) {
    console.error('Error in n8n processing:', error);
    return { error: `Error in n8n processing: ${error.message}` };
  }
}

// For standalone usage
if (typeof module !== 'undefined' && module.exports) {
  module.exports = {
    generateGitHubSearchQuery,
    generateComprehensiveSearch
  };
} else {
  // For n8n execution
  return processForN8n();
}

// Aggregate all search results
const allResults = [];
const accessKeyId = $input.first().json.accessKeyId;
const userName = $input.first().json.userName;

for (const item of $input.all()) {
  if (item.json.total_count > 0) {
    allResults.push(...item.json.items);
  }
}

// Remove duplicates based on repository and file path
const uniqueResults = allResults.filter((item, index, self) => 
  index === self.findIndex(t => t.repository.full_name === item.repository.full_name && t.path === item.path)
);

return [{
  accessKeyId,
  userName,
  totalMatches: uniqueResults.length,
  repositories: uniqueResults,
  isCompromised: uniqueResults.length > 0
}];

// n8n Function node: Extract usernames from AWS ListUsersResponse

const allInputs = $input.all();

// Find the ListUsersResponse input
const listUsersInput = allInputs.find(item => item.json.ListUsersResponse);

if (!listUsersInput) {
  throw new Error('No ListUsersResponse found in input data');
}

// Extract users array from the response
const users = listUsersInput.json
  .ListUsersResponse
  .ListUsersResult
  .Users || [];

// Extract usernames and return each as a separate item for looping
const usernameItems = users.map(user => ({
  json: { username: user.UserName }
}));

// Return array of individual username objects
return usernameItems;

// Generate comprehensive security report - Notification Only (No Automatic Actions)
const data = $input.item.json;

const report = {
  timestamp: new Date().toISOString(),
  accessKeyId: data.accessKeyId,
  userName: data.userName,
  status: data.isCompromised ? 'COMPROMISED' : 'SAFE',
  totalRepositories: data.totalMatches,
  repositories: data.repositories.map(repo => ({
    name: repo.repository.full_name,
    url: repo.html_url,
    path: repo.path,
    score: repo.score
  })),
  actionTaken: data.isCompromised ? 'Security team notified - Manual review required' : 'No action required',
  riskLevel: data.totalMatches > 5 ? 'HIGH' : data.totalMatches > 0 ? 'MEDIUM' : 'LOW',
  notificationSent: data.isCompromised ? true : false,
  requiresManualAction: data.isCompromised ? true : false,
  recommendedActions: data.isCompromised ? [
    'Manually disable the compromised access key',
    'Generate new access keys for affected services',
    'Remove exposed keys from repositories',
    'Audit recent API usage for this key'
  ] : []
};

return [report];
